import { createGlobalStyle } from "styled-components";

export const GlobalStyle = createGlobalStyle`
  html {
  font-size: 16px;
  background: #04171C;
  color: #FAFBFE;
}

a{
  text-decoration: none;
  color: #FAFBFE;
}

a:hover{
  color: #ffffff
}

@import url('https://fonts.googleapis.com/css2?family=Roboto:wght@300&display=swap');

`;
